//Reusable help wanted ad, seen on the Help Wanted and Landing pages
import React, { Component } from 'react';
import { Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button, Container, Row, Col } from 'reactstrap';
import "./WantedAd.css";
import axios from 'axios';
import ApiContext from '../ApiContext';


const btnStyle = {
    background: "#e56"
}
class WantedAd extends React.Component {

  state = {
    users: [],
    skills: [],
    jobs: [],
    skillSelected: "",
    errors: null
  };

  componentWillMount() {
    axios.get('/skills')
      .then(res => this.setState({skills: res.data}))
      .catch(err => console.log('oh boi', err));
    axios.get('/jobs')
      .then(res => {
        this.setState({jobs: res.data});
        console.log("Jobs: ", this.state.jobs);
      })
      .catch(err => console.log('oh boi', err));
  }


  render() {
    return (
      <div>
        <Card>
          <CardImg top width="100%" src="data:image/png;base64," alt="User Image" />
          <CardBody>
            <CardTitle>{this.props.userName}</CardTitle>
            <CardSubtitle></CardSubtitle>
            <CardText>
            <Container>
              {this.state.jobs.map(job => 
                <div className="job-container">
                <Row >
                  <Col sm={{ size: 12, offset: 1}}>
                    {job.jobName}
                  </Col>
                </Row>
                <Row >
                  <Col sm={{ size: 12, offset: 1}}>
                    Estimated Cost: ${job.totalCost}
                  </Col>
                </Row>
                <Row >
                  <Col sm={{ size: 12, offset: 1}}>
                    Hours Required: {job.totalHours}
                  </Col>
                </Row>
                </div>
              )}
            </Container>
            </CardText>
            <Button style={btnStyle}>Apply</Button>
          </CardBody>
        </Card>
      </div>
    );
  };
}

export default WantedAd;